    private void botaoConverterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoConverterActionPerformed
        try {
            NumberFormat format = NumberFormat.getInstance(Locale.US);
            double fahrenheit = Double.valueOf(campoFahrenheit.getText().toString());
            double celsius = (fahrenheit - 32) / 1.8;
            format.setMaximumFractionDigits(1);
            String resultado = format.format(celsius);
            campoCelsius.setText(resultado + "ÂºC");
        } catch (Exception e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_botaoConverterActionPerformed

